angular.module("yyzBannerMod",[]).directive("yyzBanner",["$http","$timeout",function(n,e){return{restrict:"E",scope:!0,template:'<div class="slide" id="slide"><ul><li ng-repeat="banner in banners"><a ng-href="{{banner.link}}"><img ng-src="{{banner.imgSrc}}" alt=""></a></li></ul><div class="dot"><span ng-repeat="banner in banners"></span></div></div>',replace:!0,link:function(i,a,r){var s=r.api;n.get(s).success(function(n){n.length>0&&(i.banners=n)}),e(function(){$("#slide").swipeSlide({continuousScroll:!0,speed:3e3,transitionType:"cubic-bezier(0.22, 0.69, 0.72, 0.88)",firstCallback:function(n,e,i){i.find(".dot").children().first().addClass("cur")},callback:function(n,e,i){i.find(".dot").children().eq(n).addClass("cur").siblings().removeClass("cur")}})},300)}}}]);