angular.module("yyzDroploadMod",[]).directive("yyzDropload",["$http",function(o){return{restrict:"E",template:"<div ng-transclude></div>",transclude:!0,replace:!0,link:function(o,r,t){$("#"+t.id).dropload({scrollArea:window,loadDownFn:function(r){$.ajax({type:"GET",url:t.api,dataType:"json",success:function(t){o.shopList=[].concat(o.shopList,t),o.$apply(),r.resetload()},error:function(o,t){alert("Ajax error!"),r.resetload()}})}})}}}]);