angular.module("yyzDirectiveMod",["oc.lazyLoad"]).directive("yyzBanner",["$http","$timeout","$ocLazyLoad",function(n,e,i){return{restrict:"E",scope:!0,template:'<div class="slide" id="slide"><ul><li ng-repeat="banner in banners"><a ng-href="{{banner.link}}"><img ng-src="{{banner.imgSrc}}" alt=""></a></li></ul><div class="dot"><span ng-repeat="banner in banners"></span></div></div>',replace:!0,link:function(a,t,l){var r=l.api;n.get(r).success(function(n){n.length>0&&(a.banners=n)}),e(function(){i.load("lib/swipeSlide/swipeSlide.min.js").then(function(){$("#slide").swipeSlide({continuousScroll:!0,speed:3e3,transitionType:"cubic-bezier(0.22, 0.69, 0.72, 0.88)",firstCallback:function(n,e,i){i.find(".dot").children().first().addClass("cur")},callback:function(n,e,i){i.find(".dot").children().eq(n).addClass("cur").siblings().removeClass("cur")}})})},300)}}}]).directive("yyzDropload",["$http","$ocLazyLoad",function(n,e){return{restrict:"E",template:"<div ng-transclude></div>",transclude:!0,replace:!0,link:function(n,i,a){e.load("lib/dropload/dist/dropload.min.js").then(function(){$("#"+a.id).dropload({scrollArea:window,loadDownFn:function(e){$.ajax({type:"GET",url:a.api,dataType:"json",success:function(i){n.shopList=[].concat(n.shopList,i),n.$apply(),e.resetload()},error:function(n,i){alert("请求数据失败!"),e.resetload()}})}})})}}}]);